import React, {useState, useEffect} from 'react';
import { auth } from "../../firebase";
import {toast, ToastContainer} from 'react-toastify'
import { useSelector } from "react-redux" // use useSelector instead of useDispatch since we don't
// want user to enter forgot password page when they are logged in.
// redirect them away

const ForgotPassword = ({history}) =>{
    const [email, setEmail] = useState('')
    const[loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true)

        const config = {
            url: process.env.REACT_APP_FORGOT_PASSWORD_REDIRECT,
            handleCodeInApp: true,
        }
        await auth.sendPasswordResetEmail(email, config)
            .then(() => {

        })
            .catch((error) =>{
                setLoading(false)
                toast.error(error.message);
                
            })

    };

    return (<div className="container col-md-6 offset-md-3 p-5">
            {loading ? <h4 className="text-danger"> Loading </h4> : <h4>Forgot Password</h4>}
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    className="form-control"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder= "Type your email"
                    autoFocus
                />
                <br/>
                <button className="btn btn-raised" type="danger" disabled={!email}>
                    Submit
                </button> {/*if user didn't enter email then button will be disabled*/}
            </form>
    </div>
    );
}

export default ForgotPassword;
