import React, {useState, useEffect} from 'react'
import AdminNav from '../../../components/navigation/AdminNav'
//import UserNav from "../../components/navigation/UserNav";
import {toast} from 'react-toastify'
import {useSelector} from "react-redux";
import {createProduct} from "../../../functions/product";
import ProductCreateForm from "../../../components/forms/ProductCreateForm";
import {getCategories, getCategorySubs} from "../../../functions/category";

const initialState = {
    title: "",
    description: "",
    price: "",
    categories: [],
    category: '',
    subs: [],
    shipping: '',
    quantity: "",
    images: [],
    seasons: ['Spring', 'Summer', 'Fall', 'Winter', 'Whole Year'],
    brands: ['Hiking', 'Visiting', 'Traveling', 'Entertaining', 'Relaxing'],
    season: '',
    brand: '',
}


const ProductCreate = () => {
    const [values, setValues] = useState(initialState);
    const [subOptions, setSubOptions] = useState([]);
    const [showSub, setShowSub] = useState(false);

    //redux
    const {user} = useSelector((state) => ({...state}));

    useEffect(() => {
        loadCategories().then().catch();
    }, []);

    const loadCategories = () => getCategories().then(c =>
        setValues({...values, categories: c.data})
    );




    const handleSubmit = (e) => {
        e.preventDefault();
        createProduct(values, user.token).then(
            (res) => {
                //console.log(res);

                window.alert(`"${res.data.title}" is created`);
                //toast.success(`"${res.data.title}" is created`);
                window.location.reload();
            })
            .catch((err) => {
            console.log(err)

            /*if(err.response.status === 400)
                toast.error(err.response.data);*/

                toast.error(err.response.data.err);

        });
    };



    const  handleChange = (e) => {
        setValues({...values, [e.target.name] : e.target.value});
        //console.log(e.target.name, ' ----- ', e.target.value);
    };

    const handleCategoryChange = (e) => {
        e.preventDefault()
        console.log('CLICKED CATEGORY', e.target.value)
        setValues({...values, category : e.target.value});
        getCategorySubs(e.target.value).then(res => {
            console.log('SUBS OPTION ON CATEGORY CLICK', res);
            setSubOptions(res.data);
        });
    };

    return(
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-2">
                    <AdminNav />
                </div>
                <div className="col-md-10">
                    <h4>Product create</h4>
                    <hr/>
                    {/*{JSON.stringify(values)}*/}
                    {/*{JSON.stringify(values.categories)}*/}
                        JSON.stringify(valu
                    <ProductCreateForm
                        handleSubmit={handleSubmit}
                        handleChange={handleChange}
                        values={values}
                        handleCategoryChange={handleCategoryChange}
                        subOptions={subOptions}
                        showSub={showSub}
                    />
                </div>
            </div>
        </div>
    )
}

export default ProductCreate;
