import React, {useState} from 'react'
import {Menu} from 'antd';
import { HomeOutlined, SettingOutlined, UserOutlined, UserAddOutlined } from '@ant-design/icons';
import Home from "../../pages/Home";
import {Link} from 'react-router-dom';
import firebase from 'firebase';
import {useDispatch} from "react-redux";


const { SubMenu, Item } = Menu; // Menu.SubMenu

const Header = ({history}) => {
    const [current, setCurrent] = useState("home");
    let dispatch = useDispatch()
    const handleClick = (e) => {
        //console.log(e.key);
        setCurrent(e.key);
    };

    const logout = () => {
        firebase.auth().signOut()
        dispatch({
            type: "LOGOUT",
            payload: null,
        });
        history.push('/login');
    };
    return(
        <Menu onClick={handleClick} selectedKeys={[current]} mode="horizontal">
            {/* Icons for Navigation components*/}
            <Item key="home" icon={<HomeOutlined />}>
                <Link to="/">Home</Link> {/*Link to = "" equals to href ="" (go to another page)*/}
            </Item>

            <Item key="register" icon={<UserAddOutlined />} className="float-right">
                <Link to="/register">Register</Link>
            </Item>

            {/* className = "float-right" to move to right side of Navbar (remember to add Bootstrap first) */}
            <Item key="login" icon={<UserOutlined />} className="float-right">
                <Link to="/login">Login</Link>
            </Item>



            <SubMenu key="SubMenu" icon={<SettingOutlined />} title="Username">

                    <Item key="setting:1">Option 1</Item>
                    <Item key="setting:2">Option 2</Item>
                    <Item icon={ <UserOutlined/>} onClick={logout}>Logout</Item>


            </SubMenu>

        </Menu>
    )
}

export default Header;
